mapper.insert:
insert into
    feed(
        uno

        , pno


        , eno

        , content
        , ctype

        , thumbnail

        , tag
    )
(select
    #{uno}

    , #{pno}


    , #{eno}

    , #{content}
    , #{ctype}

    , (select thumbnail from program where pno = #{pno})


    , (select thumbnail from episode where eno = #{eno})

    , #{tag}
from
    dual)

mapper.update:
update
    feed
set
    content = #{content}
    , ctype = #{ctype}
    , tag   = #{tag}
where
    fno = #{fno}
