mapper.insert:
insert into
    program_like(
        uno
        , pno
    )
values(
    #{uno}
    , #{tno}
)

insert into
    program_reply_like(
        uno
        , prno
    )
values(
    #{uno}
    , #{tno}
)

insert into
    episode_like(
        uno
        , eno
    )
values(
    #{uno}
    , #{tno}
)

insert into
    episode_reply_like(
        uno
        , erno
    )
values(
    #{uno}
    , #{tno}
)

insert into
    feed_like(
        uno
        , fno
    )
values(
    #{uno}
    , #{tno}
)

insert into
    feed_reply_like(
        uno
        , frno
    )
values(
    #{uno}
    , #{tno}
)

mapper.delete:
delete from
    program_like
where
    pno = #{tno}
    and
    uno = #{uno}

delete from
    program_reply_like
where
    prno = #{tno}
    and
    uno = #{uno}

delete from
    episode_like
where
    eno = #{tno}
    and
    uno = #{uno}

delete from
    episode_reply_like
where
    erno = #{tno}
    and
    uno = #{uno}

delete from
    feed_like
where
    fno = #{tno}
    and
    uno = #{uno}

delete from
    feed_reply_like
where
    frno = #{tno}
    and
    uno = #{uno}
