mapper.select:
select *
from user
where email = #{email}
    and password = #{password}

select count(*)
from user
where email = #{email}
    and password = #{password}

select count(*)
from user
where BINARY nick_name = #{nick_name}

mapper.insert:
insert into
    user(
        email
        , password
        , nick_name
    )
values(
    #{email}
    , #{password}
    , #{nick_name}
)

insert into
    user(
        email
        , nick_name
        , is_certification
        , password
    )
values(
    #{email}
    , #{nick_name}
    , true
    , "0"
)

mapper.update:
update user
set password = #{password}
where email = #{email}

update user
set nick_name = #{nick_name},
    bio = #{bio},
    profile_pic = #{profile_pic}
where email = #{email}

update user
set is_certification = true
where email = #{email}
