mapper.select:
select *
from user
where email = #{email}
    and password = #{password}

select count(*)
from user
where email = #{email}
    and password = #{password}

mapper.insert:
insert into
    user(
        email
        , password
        , nick_name
    )
values(
    #{email}
    , #{password}
    , #{nick_name}
)

mapper.update:
update user
set password = #{password}
where email = #{email}
